<script lang="ts">
  import { onMount } from 'svelte';
  import { Alert, Badge, Indicator, Heading } from 'flowbite-svelte';
  import { invoke } from '@tauri-apps/api/tauri';
  import { P, GradientButton, Toast, Input, Label } from 'flowbite-svelte';
  import { CheckCircleSolid } from 'flowbite-svelte-icons';
  import { slide } from 'svelte/transition';

  let endpoints: boolean = false;
  let available = 'Unavailable';
  let badgeColorClass = 'bg-red-500';
  let indicatorColor = 'red';
  let toastStatus = false;
  let counter = 5;

  async function test_endpoints() {
    endpoints = await invoke('test_endpoints');
    invoke('log_message', { message: "endpoints message: " + endpoints });
    if (endpoints) {
      badgeColorClass = 'bg-green-500';
      indicatorColor = 'green';
      available = 'Available';
      toastStatus = true;
      timeout();
    } else {
      badgeColorClass = 'bg-red-500';
      indicatorColor = 'red';
      available = 'Unavailable';
    }
  }

  function timeout() {
    if (--counter > 0) return setTimeout(timeout, 1000);
    toastStatus = false;
    goto('/ops');
  }

  function goto(url: string) {
    const link = document.createElement('a');
    link.href = url;
    link.style.display = 'none';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  }
</script>

